import MyModal from "./UI/MyModal/MyModal";
import TaskUpdate from "./TaskUpdate";
import format from 'date-fns/format';
import {  } from '@mui/material';

const TaskItem = (props) => {
    const [modal, setModal] = useState(false);

    const taskUpdated = () => {
        setModal(false)
    }

    const columns = [
        {
            field: 'title',
            headerName: 'Title',
            width: 150,
        },
        {
            field: 'date',
            headerName: 'Date',
            width: 150,
        },
        {
            field: 'status',
            headerName: 'Status',
            width: 110,
        },
        {
            field: 'body',
            headerName: 'Description',
            width: 160,
        },
    ];

    const rows = [
        { title: props.task.title, date: props.task.date, status: props.task.status, body: props.task.body },
    ];

    return (
        <div style={{ height: 400, width: '100%' }}>
            <DataGrid
                rows={rows}
                columns={columns}
