import {useState} from "react";
import TasksList from "./сomponents/TasksList";
import './styles/App.css'
import MyInput from "./сomponents/UI/input/MyInput";
import MyButton from "./сomponents/UI/button/MyButton";
import TaskForm from "./сomponents/TaskForm";

function App() {

    const [tasks, setTasks] = useState([
        { id: 1, date: '2022-01-01', status: 'completed', title: 'Task 1', body: 'Task Body 1' },
        { id: 2, date: '2022-02-01', status: 'in progress', title: 'Task 2', body: 'Task Body 2' },
        { id: 3, date: '2022-03-01', status: 'pending', title: 'Task 3', body: 'Task Body 3' },
        { id: 4, date: '2022-03-01', status: 'completed', title: 'TEST', body: 'Task Body 4' },
    ]);

    const createTask = (newTask) => {
        setTasks([...tasks, newTask])
    }

    const handleDelete = (taskId) => {
        const dueDate = new Date(tasks.find((task) => task.id === taskId).date);
        const today = new Date();
        if (dueDate < today.setDate(today.getDate() - 6)) {
            const updatedTasks = tasks.filter((task) => task.id !== taskId);
            setTasks(updatedTasks);
        } else {
            alert("Task can't be deleted as it's due date is less than 6 days from today.");
        }
    };


    return(
        <div>
            <TaskForm create={createTask}></TaskForm>
            {tasks.length !== 0
            }
            <TasksList remove={handleDelete} tasks={tasks}/>
        </div>
    );


}

export default App;
