import React, {useState} from 'react';
import input from "./UI/input/MyInput";
import MyButton from "./UI/button/MyButton";
import {useForm} from "react-hook-form";
import Button from '@mui/material/Button';
import {InputLabel, NativeSelect, Input} from "@mui/material";
import MyInput from "./UI/input/MyInput";


const TaskForm = ({create}) => {

    const [task, setTask] = useState({title: '', body: '', status: '', date:''});

    const addNewTask = (e) => {
        e.preventDefault()
        const newTask = {
            ...task
        }
        create(newTask)

        setTask({title: '', body: '', status: '', date:''})

    }

    const {
        register, formState: {
            errors, isValid
        }, handleSubmit, reset
    } = useForm();

    const onSubmit = () => {
        const newTask = {
            ...task
        }
        create(newTask)

        setTask({title: '', body: '', status: '', date:''})
        reset();
    }

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <InputLabel>Task name
                <input
                    {...register("title", {required: "this field is required"})}
                    value={task.title}
                    onChange={e => setTask({...task, title: e.target.value})}
                    type="text"
                    placeholder="Task name"

                ></input>
            </InputLabel>
                <div style={{height:40}}>{errors?.title && <p>{errors?.title?.message || "Error!"}</p>}</div>
            <InputLabel>Task description
                <input
                    {...register("body", {required: "this field is required"})}
                    value={task.body}
                    onChange={e => setTask({...task, body: e.target.value})}
                    type="text"
                    placeholder="Task description"

                ></input>
            </InputLabel>
                <div style={{height:40}}>{errors?.body && <p>{errors?.body?.message || "Error!"}</p>}</div>
            <InputLabel>Task status
                <select
                    {...register("status", {required: "this field is required"})}
                    onChange={(e) => setTask({...task, status: e.target.value})
                }>
                    <option value="" selected disabled hidden>Choose status</option>
                    <option value="pending">Pending</option>
                    <option value="completed">Completed</option>
                    <option value="in progress">In Progress</option>
                </select>
            </InputLabel><br/>
            <div style={{height:40}}>{errors?.status && <p>{errors?.status?.message || "Error!"}</p>}</div>
            <InputLabel>Task deadline
                <input
                    {...register("date", {required: "this field is required"})}
                    type="date"
                    onChange={(e) => setTask({...task, date: e.target.value})}
                />
            </InputLabel>
            <div style={{height:40}}>{errors?.date && <p>{errors?.date?.message || "Error!"}</p>}</div>
            <Input type="submit" value="Create task"/>
            <MyButton variant="contained">Create task</MyButton>

        </form>
    );
};

export default TaskForm;
