import React, {useState} from 'react';
import MyInput from "./UI/input/MyInput";
import MyButton from "./UI/button/MyButton";

const TaskForm = ({create}) => {

    const [task, setTask] = useState({title: '', body: '', status: ''});

    const addNewTask = (e) => {
        e.preventDefault()
        const newTask = {
            ...task
        }
        create(newTask)
        setTask({title: '', body: '', status: ''})

    }

    return (
        <form>
            <MyInput
                value={task.title}
                onChange={e => setTask({...task, title: e.target.value})}
                type="text"
                placeholder="Task name"

            ></MyInput>
            <MyInput
                value={task.body}
                onChange={e => setTask({...task, body: e.target.value})}
                type="text"
                placeholder="Task description"

            ></MyInput>
            <select onChange={(e) => setTask({...task, status: e.target.value})}>
                <option value="pending">Pending</option>
                <option value="completed">Completed</option>
                <option value="in progress">In Progress</option>
            </select>
            <MyInput type="date" onChange={(e) => setTask({...task, date: e.target.value})} />
            <MyButton onClick={addNewTask}>Create task</MyButton>

        </form>
    );
};

export default TaskForm;
