import React, {useState} from 'react';
import MyInput from "./UI/input/MyInput";
import MyButton from "./UI/button/MyButton";

const TaskForm = ({create}) => {

    const [task, setTask] = useState({title: '', body: '', status: ''});

    const addNewTask = (e) => {
        e.preventDefault()
        const newTask = {
            ...task
        }
        create(newTask)
        setTask({title: '', body: '', status: ''})

    }

    return (
        <form>
            <label>Task name
                <MyInput
                    value={task.title}
                    onChange={e => setTask({...task, title: e.target.value})}
                    type="text"
                    placeholder="Task name"
                    required = {true}
                ></MyInput>
            </label>
            <label>Task description
                <MyInput
                    value={task.body}
                    onChange={e => setTask({...task, body: e.target.value})}
                    type="text"
                    placeholder="Task description"
                    required = {true}
                ></MyInput>
            </label>
            <label>Task status <br/>
                <select onChange={(e) => setTask({...task, status: e.target.value})} required = {true}>
                    <option value="pending">Pending</option>
                    <option value="completed">Completed</option>
                    <option value="in progress">In Progress</option>
                </select>
            </label>
            <label>Task deadline
                <MyInput type="date" onChange={(e) => setTask({...task, date: e.target.value})} required = {true}/>
            </label>
            <MyButton onClick={addNewTask}>Create task</MyButton>

        </form>
    );
};

export default TaskForm;
