import React, {useState} from 'react';
import MyButton from "./UI/button/MyButton";
import TaskForm from "./TaskForm";
import MyModal from "./UI/MyModal/MyModal";
import TaskUpdate from "./TaskUpdate";

const TaskItem = (props) => {
    const [modal, setModal] = useState(false);
    //handleEdit(props.task.id, 'new text', 'completed')

    return (
        <div>
            <li className="Task_block" key={props.task.id}>
                <p>{props.task.title}</p>
                <p>{props.task.date}</p>
                <p>{props.task.status}</p>
                <p>{props.task.body}</p>
                <MyButton  onClick={() => setModal(true)}>Edit</MyButton>
                <MyModal visible={modal} setVisible={setModal}
                >
                    <TaskUpdate create={props.update()} taskId={props.task.id} ></TaskUpdate>
                </MyModal>
                <MyButton  onClick={() => props.remove(props.task.id)}>Delete</MyButton>
            </li>
        </div>
    );
};

export default TaskItem;
