import React, {useState} from 'react';
import TaskItem from "./TaskItem";

const TasksList = ({tasks, remove}) => {

    if (tasks.length == 0){
        return (
            <h1 style={{textAlign: "center"}}>
                No tasks
            </h1>
        )
    }

    return (
        <div>
                {tasks.map((task) =>
                    <TaskItem remove={remove} task = {task} key={task.id}/>
                )}
        </div>
    );
};

export default TasksList;
