import React, {useState} from 'react';
import MyButton from "./UI/button/MyButton";
import TaskForm from "./TaskForm";
import MyModal from "./UI/MyModal/MyModal";
import TaskUpdate from "./TaskUpdate";
import format from 'date-fns/format';

const TaskItem = (props) => {
    const [modal, setModal] = useState(false);

    const taskUpdated = () => {
        setModal(false)
    }

    const columns = [
        {
            field: 'firstName',
            headerName: 'First name',
            width: 150,
            editable: true,
        },
        {
            field: 'lastName',
            headerName: 'Last name',
            width: 150,
            editable: true,
        },
        {
            field: 'age',
            headerName: 'Age',
            type: 'number',
            width: 110,
            editable: true,
        },
        {
            field: 'fullName',
            headerName: 'Full name',
            description: 'This column has a value getter and is not sortable.',
            sortable: false,
            width: 160,
            valueGetter: (params) =>
                `${params.getValue(params.id, 'firstName') || ''} ${
                    params.getValue(params.id, 'lastName') || ''
                }`,
        },
    ];

    return (
            <tr key={props.task.id}>
                <th>{props.task.title}</th>
                <th>{format(new Date(props.task.date), 'dd-MM-Y')}</th>
                <th>{props.task.status}</th>
                <th>{props.task.body}</th>
                <th><MyButton  onClick={() => setModal(true)}>Edit</MyButton></th>
                <MyModal visible={modal} setVisible={setModal}>
                    <TaskUpdate update={props.update} taskId={props.task.id} modal={taskUpdated} ></TaskUpdate>
                </MyModal>
                <th><MyButton  onClick={() => props.remove(props.task.id)}>Delete</MyButton></th>
            </tr>
    );
};

export default TaskItem;
