import {useMemo, useState} from "react";
import TasksList from "./сomponents/TasksList";
import './styles/App.css'
import MyInput from "./сomponents/UI/input/MyInput";
import MyButton from "./сomponents/UI/button/MyButton";
import TaskForm from "./сomponents/TaskForm";
import MySelect from "./сomponents/UI/select/MySelect";
import TaskFilter from "./сomponents/TaskFilter";
import MyModal from "./сomponents/UI/MyModal/MyModal";

function App() {

    const [tasks, setTasks] = useState([
        {id: 1, date: '2023-01-01', status: 'completed', title: 'Task 1', body: 'Task Body 1'},
        {id: 2, date: '2023-02-02', status: 'in progress', title: 'Task 2', body: 'Task Body 2'},
        {id: 3, date: '2023-03-03', status: 'pending', title: 'Task 3', body: 'Task Body 3'},
        {id: 4, date: '2023-04-04', status: 'completed', title: 'TEST', body: 'Task Body 4'},
    ]);


    const [filter, setFilter] = useState({ date: '', status: '', sort: '' });
    const [modal, setModal] = useState(false);

    const filteredSortTasks = useMemo(()=> {
        let filteredTasks = sortedTasks;
        if (filter.date) { console.log(filter.date)
            filteredTasks = filteredTasks.filter((task) => task.date <= filter.date);
        }
        if (filter.status) {
            filteredTasks = filteredTasks.filter((task) => task.status === filter.status);
        }
        return filteredTasks;
    }, [sortedTasks, filter.date, filter.status])


    const createTask = (newTask) => {
        setTasks([...tasks, newTask])
        setModal(false)
    }

    const handleDelete = (taskId) => {
        const dueDate = new Date(tasks.find((task) => task.id === taskId).date);
        const today = new Date();
        if (dueDate > today.setDate(today.getDate() + 6) || dueDate < today) {
            const updatedTasks = tasks.filter((task) => task.id !== taskId);
            setTasks(updatedTasks);
        } else {
            alert("Task can't be deleted as it's due date is less than 6 days from today.");
        }
    };




    return (
        <div>
            <MyButton style={{marginTop: "30px"}} onClick={()=>setModal(true)}>
                Add task
            </MyButton>

            <MyModal visible={modal} setVisible={setModal}
            >
                <TaskForm create={createTask}></TaskForm>
            </MyModal>
            <TaskFilter
                filter={filter}
                setFilter={setFilter}
            ></TaskFilter>

            <TasksList remove={handleDelete} tasks={filteredSortTasks}/>


        </div>
    );


}

export default App;
