import axios from "axios";

export default class TaskService {
    static async getAll(){
        const response = await axios.get('https://vik-bb.ru/api/tasks')
        return response.data.data;
    }

    static async update({task}){
        const response = await axios.put('https://vik-bb.ru/api/tasks/'+task.id, {
            status: task.status,
            body: task.body
        })
        return response.data.data;
    }

    static async delete({taskid}){
        const response = await axios.delete('https://vik-bb.ru/api/tasks'+taskid)
        return response.data.data;
    }
}
