import React, {useState} from 'react';

const TasksList = () => {

    const [tasks, setTasks] = useState([
        { id: 1, date: '2022-01-01', status: 'completed', title: 'Task 1', body: 'Task Body 1' },
        { id: 2, date: '2022-02-01', status: 'in progress', title: 'Task 2', body: 'Task Body 2' },
        { id: 3, date: '2022-03-01', status: 'pending', title: 'Task 3', body: 'Task Body 3' },
    ]);

    const handleEdit = (taskId, newTitle, newStatus) => {
        const updatedTasks = tasks.map((task) => {
            if (task.id === taskId) {
                return { ...task, title: newTitle, status: newStatus };
            }
            return task;
        });
        setTasks(updatedTasks);
    };

    const handleDelete = (taskId) => {
        const dueDate = new Date(tasks.find((task) => task.id === taskId).date);
        const today = new Date();
        if (dueDate < today.setDate(today.getDate() - 6)) {
            const updatedTasks = tasks.filter((task) => task.id !== taskId);
            setTasks(updatedTasks);
        } else {
            alert("Task can't be deleted as it's due date is less than 6 days from today.");
        }
    };


    return (
        <div className="TasksList">
                <ul className="Task_content">
                    {tasks.map((task) => (
                        <li className="Task_block" key={task.id}>
                            <p>{task.title}</p>
                            <p>{task.date}</p>
                            <p>{task.status}</p>
                            <p>{task.body}</p>
                            <button className="Task_button" onClick={() => handleEdit(task.id, 'new text', 'completed')}>Edit</button>
                            <button className="Task_button" onClick={() => handleDelete(task.id)}>Delete</button>
                        </li>
                    ))}
                </ul>
        </div>
    );
};

export default TasksList;
