import React, {useState} from 'react';
import TaskItem from "./TaskItem";
import { Table } from '@mui/material';
import {DataGrid} from "@mui/x-data-grid";

const TasksList = ({tasks, remove, update}) => {

    if (tasks.length == 0){
        return (
            <h1 style={{textAlign: "center"}}>
                No tasks
            </h1>
        )
    }



    const columns = [
        {
            field: 'title',
            headerName: 'Title',
            width: 150,
        },
        {
            field: 'date',
            headerName: 'Date',
            width: 150,
        },
        {
            field: 'status',
            headerName: 'Status',
            width: 110,
        },
        {
            field: 'body',
            headerName: 'Description',
            width: 160,
        },
    ];

    const fillRows = (tasks) => {
        let rows;
        {tasks.map((task) =>
            rows.push({ title: task.title, date: task.date, status: task.status, body: task.body })
        )}
        let rows1 = rows;
        return rows1;
    }

    // const rows = [
    //      { title: props.task.title, date: props.task.date, status: props.task.status, body: props.task.body }
    // ];

    const rows = fillRows(tasks);

    return (

        <div style={{ height: 400, width: '100%' }}>
            <DataGrid
                rows={rows}
                columns={columns}
                pageSize={5}
                rowsPerPageOptions={[5]}
                //checkboxSelection
                //disableSelectionOnClick
            />
        </div>



        // <Table>
        //         {tasks.map((task) =>
        //             <TaskItem update={update} remove={remove} task = {task} key={task.id}/>
        //         )}
        //</Table>
    );
};

export default TasksList;
