import React from 'react';
import MyButton from "./UI/button/MyButton";

const TaskItem = (props) => {

    const handleEdit = (taskId, newTitle, newStatus) => {
        // const updatedTasks = tasks.map((task) => {
        //     if (task.id === taskId) {
        //         return { ...task, title: newTitle, status: newStatus };
        //     }
        //     return task;
        // });
        // setTasks(updatedTasks);
    };



    return (
        <div>
            <li className="Task_block" key={props.task.id}>
                <p>{props.task.id}</p>
                <p>{props.task.title}</p>
                <p>{props.task.date}</p>
                <p>{props.task.status}</p>
                <p>{props.task.body}</p>
                <MyButton  onClick={() => handleEdit(props.task.id, 'new text', 'completed')}>Edit</MyButton>
                <MyButton  onClick={() => props.remove(props.task.id)}>Delete</MyButton>
            </li>
        </div>
    );
};

export default TaskItem;
