import {useState} from "react";
import TasksList from "./сomponents/TasksList";
import './styles/App.css'
import MyInput from "./сomponents/UI/input/MyInput";
import MyButton from "./сomponents/UI/button/MyButton";

function App() {

    const [tasks, setTasks] = useState([
        { id: 1, date: '2022-01-01', status: 'completed', title: 'Task 1', body: 'Task Body 1' },
        { id: 2, date: '2022-02-01', status: 'in progress', title: 'Task 2', body: 'Task Body 2' },
        { id: 3, date: '2022-03-01', status: 'pending', title: 'Task 3', body: 'Task Body 3' },
        { id: 4, date: '2022-03-01', status: 'completed', title: 'TEST', body: 'Task Body 4' },
    ]);

    const [task, setTask] = useState({title : '', body: '', status: ''});

    const addNewTask = (e)=>{
        e.preventDefault()
        setTasks([...tasks, {...task}])
        console.log()
        setTask({title: '', body: '', status: ''})

    }


    return(
        <div>
            <form>
                <MyInput
                    value={task.title}
                    onChange={e => setTask({...task, title: e.target.value})}
                    type="text"
                    placeholder="Task name"

                ></MyInput>
                <MyInput
                    value={task.body}
                    onChange={e => setTask({...task, body: e.target.value})}
                    type="text"
                    placeholder="Task description"

                ></MyInput>
                <MyInput
                    value={task.status}
                    onChange={e => setTask({...task, status: e.target.value})}
                    type="text"
                    placeholder="Task status"

                ></MyInput>
                <MyButton onClick={addNewTask}>Create task</MyButton>

            </form>

            <TasksList tasks={tasks}/>
        </div>
    );


}

export default App;
