import React, {useState} from 'react';
import TaskItem from "./TaskItem";
import { Table } from '@mui/material';
import {DataGrid} from "@mui/x-data-grid";

const TasksList = ({tasks, remove, update}) => {

    const [rows, setRows] = useState([]);


    if (tasks.length == 0){
        return (
            <h1 style={{textAlign: "center"}}>
                No tasks
            </h1>
        )
    }



    const columns = [
        {
            field: 'title',
            headerName: 'Title',
            width: 150,
        },
        {
            field: 'date',
            headerName: 'Date',
            width: 150,
        },
        {
            field: 'status',
            headerName: 'Status',
            width: 110,
        },
        {
            field: 'body',
            headerName: 'Description',
            width: 160,
        },
    ];

    const fillRows = (tasks) => {

        {tasks.map((task) =>
            setRows([...rows, { title: task.title, date: task.date, status: task.status, body: task.body }])
        )}
        return rows;
    }

    // const rows = [
    //      { title: props.task.title, date: props.task.date, status: props.task.status, body: props.task.body }
    // ];

    const rowsFills = fillRows(tasks);

    const test = { title: "test", date: '12-01-2023', status: "in progress", body: "tes tes tse test sdf sdf " };

    return (

        <div style={{ height: 400, width: '100%' }}>
            <DataGrid
                rows={test}
                columns={columns}
                pageSize={5}
                rowsPerPageOptions={[5]}
            />
        </div>



        // <Table>
        //         {tasks.map((task) =>
        //             <TaskItem update={update} remove={remove} task = {task} key={task.id}/>
        //         )}
        //</Table>
    );
};

export default TasksList;
